// src/components/UserHome.js
import React, { useEffect, useState, useCallback } from 'react';
import { Home, LogOut, Menu, CreditCard, Send, TrendingUp, Settings } from 'lucide-react';
import PaymentForm from './PaymentForm';
import Statements from './Statements';
import FinancialInsights from './FinancialInsights';
import { getBalanceAndTransactions, getUserProfile } from '../api';
import { useNavigate } from 'react-router-dom';
import Chart from 'chart.js/auto';

const UserHome = ({ token }) => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [accountNumber, setAccountNumber] = useState('');
  const [userName, setUserName] = useState('');
  const [selectedTab, setSelectedTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const fetchUserData = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const balanceResponse = await getBalanceAndTransactions(token);
      setBalance(balanceResponse.data.balance);
      setTransactions(balanceResponse.data.transactions);

      const profileResponse = await getUserProfile(token);
      setAccountNumber(profileResponse.data.accountNumber);
      setUserName(`${profileResponse.data.name} ${profileResponse.data.surname}`);
    } catch (error) {
      setError('Could not load data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchUserData();
  }, [fetchUserData]);

  const tabs = [
    { id: 'dashboard', label: 'Dashboard', icon: <Home size={20} /> },
    { id: 'makePayment', label: 'Make a Payment', icon: <Send size={20} /> },
    { id: 'statements', label: 'Statements', icon: <CreditCard size={20} /> },
    { id: 'insights', label: 'Financial Insights', icon: <TrendingUp size={20} /> },
    { id: 'settings', label: 'Settings', icon: <Settings size={20} /> },
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="flex h-screen overflow-hidden">
      {/* Sidebar */}
      <div
        className={`fixed inset-y-0 left-0 transform ${
          sidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } transition-transform duration-300 ease-in-out z-30 w-64 bg-white shadow-lg`}
      >
        <div className="flex flex-col h-full">
          <div className="flex items-center justify-center h-16 bg-gradient-to-r from-orange-500 to-red-500">
            <h1 className="text-2xl font-bold text-white">Customer Portal</h1>
          </div>
          <nav className="flex-1 px-2 py-4 bg-white overflow-y-auto">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => {
                  setSelectedTab(tab.id);
                  setSidebarOpen(false);
                }}
                className={`flex items-center w-full px-4 py-2 mt-2 text-sm font-medium text-left rounded-lg ${
                  selectedTab === tab.id
                    ? 'bg-orange-500 text-white'
                    : 'text-gray-700 hover:bg-orange-100'
                }`}
              >
                {React.cloneElement(tab.icon, {
                  className: 'w-5 h-5 mr-3',
                  color: selectedTab === tab.id ? '#FFFFFF' : '#FF4500',
                })}
                {tab.label}
              </button>
            ))}
          </nav>
          <div className="p-4">
            <button
              onClick={handleLogout}
              className="flex items-center w-full px-4 py-2 text-sm font-medium text-left text-gray-700 rounded-lg hover:bg-red-100"
            >
              <LogOut className="w-5 h-5 mr-3" />
              Logout
            </button>
          </div>
        </div>
      </div>

      {/* Content area */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top Navigation */}
        <header className="flex items-center justify-between px-6 py-4 bg-white shadow-md">
          <div className="flex items-center">
            <button
              onClick={() => setSidebarOpen(!sidebarOpen)}
              className="text-gray-500 focus:outline-none lg:hidden"
            >
              <Menu className="w-6 h-6" />
            </button>
            <h2 className="ml-4 text-2xl font-semibold text-orange-600 hidden lg:block capitalize">
              {selectedTab.replace(/([A-Z])/g, ' $1').trim()}
            </h2>
          </div>
          <div className="flex items-center">
            <span className="text-gray-700 mr-4">Hello, {userName}</span>
            <div className="relative">
              {/* User avatar or icon can go here */}
            </div>
          </div>
        </header>

        {/* Main Content */}
        <main className="flex-1 overflow-y-auto bg-gray-50">
          {loading ? (
            <div className="flex items-center justify-center h-full">
              <svg
                className="animate-spin h-10 w-10 text-orange-500"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
              >
                <circle
                  className="opacity-25"
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="4"
                ></circle>
                <path
                  className="opacity-75"
                  fill="currentColor"
                  d="M4 12a8 8 0 018-8v8H4z"
                ></path>
              </svg>
            </div>
          ) : error ? (
            <div className="flex items-center justify-center h-full">
              <p className="text-red-500">{error}</p>
            </div>
          ) : (
            <>
              {selectedTab === 'dashboard' && (
                <div className="p-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6">
                    {/* Account Balance Card */}
                    <div className="bg-white shadow-lg rounded-lg p-6">
                      <h3 className="text-xl font-semibold text-gray-700 mb-4">
                        Account Balance
                      </h3>
                      <p className="text-4xl font-bold text-orange-600">
                        R{balance.toFixed(2)}
                      </p>
                      <div className="mt-4">
                        <button
                          onClick={() => setSelectedTab('makePayment')}
                          className="px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 transition"
                        >
                          Make a Payment
                        </button>
                      </div>
                    </div>
                    {/* Account Number Card */}
                    <div className="bg-white shadow-lg rounded-lg p-6">
                      <h3 className="text-xl font-semibold text-gray-700 mb-4">
                        Account Number
                      </h3>
                      <p className="text-2xl font-bold text-orange-600">
                        {accountNumber}
                      </p>
                    </div>
                    {/* Recent Transactions */}
                    <div className="bg-white shadow-lg rounded-lg p-6 md:col-span-2 xl:col-span-1">
                      <h3 className="text-xl font-semibold text-gray-700 mb-4">
                        Recent Transactions
                      </h3>
                      <ul className="divide-y divide-gray-200">
                        {transactions.slice(0, 5).map((transaction) => (
                          <li
                            key={transaction._id}
                            className="py-2 flex justify-between"
                          >
                            <span className="text-gray-700">
                              {transaction.displayText}
                            </span>
                            <span
                              className={`font-medium ${
                                transaction.transactionType === 'incoming'
                                  ? 'text-green-600'
                                  : 'text-red-600'
                              }`}
                            >
                              R{transaction.amount}
                            </span>
                          </li>
                        ))}
                      </ul>
                      <div className="mt-4 text-right">
                        <button
                          onClick={() => setSelectedTab('statements')}
                          className="text-orange-600 hover:underline"
                        >
                          View All
                        </button>
                      </div>
                    </div>
                    {/* Financial Insights */}
                    <div className="bg-white shadow-lg rounded-lg p-6 md:col-span-2 xl:col-span-2">
                      <h3 className="text-xl font-semibold text-gray-700 mb-4">
                        Spending Overview
                      </h3>
                      <canvas id="spendingChart"></canvas>
                    </div>
                  </div>
                </div>
              )}

              {selectedTab === 'makePayment' && (
                <div className="p-6">
                  <PaymentForm
                    token={token}
                    onTransactionUpdate={fetchUserData}
                    balance={balance}
                  />
                </div>
              )}

              {selectedTab === 'statements' && (
                <div className="p-6">
                  <Statements transactions={transactions} />
                </div>
              )}

              {selectedTab === 'insights' && (
                <div className="p-6">
                  <FinancialInsights transactions={transactions} />
                </div>
              )}

              {selectedTab === 'settings' && (
                <div className="p-6">
                  <h3 className="text-2xl font-semibold text-gray-700 mb-6">
                    Settings
                  </h3>
                  {/* Settings Form */}
                  <form className="space-y-6 max-w-lg">
                    <div>
                      <label className="block text-gray-700">First Name</label>
                      <input
                        type="text"
                        value={userName.split(' ')[0]}
                        className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                        disabled
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700">Last Name</label>
                      <input
                        type="text"
                        value={userName.split(' ')[1]}
                        className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                        disabled
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700">Email</label>
                      <input
                        type="email"
                        value={accountNumber}
                        className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                        disabled
                      />
                    </div>
                    <div>
                      <label className="block text-gray-700">Change Password</label>
                      <input
                        type="password"
                        placeholder="Enter new password"
                        className="w-full mt-1 p-2 border border-gray-300 rounded-lg"
                      />
                    </div>
                    <button
                      type="submit"
                      className="px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 transition"
                    >
                      Save Changes
                    </button>
                  </form>
                </div>
              )}
            </>
          )}
        </main>
      </div>

      {/* Chart.js Initialization */}
      <script>
        {`
          document.addEventListener('DOMContentLoaded', function() {
            const ctx = document.getElementById('spendingChart').getContext('2d');
            const spendingChart = new Chart(ctx, {
              type: 'line',
              data: {
                labels: ${JSON.stringify(
                  transactions.map((t) =>
                    new Date(t.createdAt).toLocaleDateString()
                  )
                )},
                datasets: [{
                  label: 'Spending Over Time',
                  data: ${JSON.stringify(transactions.map((t) => t.amount))},
                  borderColor: 'rgba(255, 99, 132, 1)',
                  backgroundColor: 'rgba(255, 159, 64, 0.2)',
                  tension: 0.1
                }]
              },
              options: {
                responsive: true,
                scales: {
                  x: {
                    display: true,
                    title: {
                      display: true,
                      text: 'Date'
                    }
                  },
                  y: {
                    display: true,
                    title: {
                      display: true,
                      text: 'Amount (R)'
                    }
                  }
                }
              }
            });
          });
        `}
      </script>
    </div>
  );
};

export default UserHome;
