// src/components/UserHome.js
import React, { useEffect, useState, useCallback } from 'react';
import { DollarSign, FileText } from 'lucide-react';
import PaymentForm from './PaymentForm';
import Statements from './Statements';
import { getBalanceAndTransactions, getUserProfile } from '../api';

const UserHome = ({ token }) => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [accountNumber, setAccountNumber] = useState('');
  const [selectedTab, setSelectedTab] = useState('makePayment');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchUserBalanceAndTransactions = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const balanceResponse = await getBalanceAndTransactions(token);
      setBalance(balanceResponse.data.balance);
      setTransactions(balanceResponse.data.transactions);

      const profileResponse = await getUserProfile(token);
      setAccountNumber(profileResponse.data.accountNumber);
    } catch (error) {
      setError('Could not load data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchUserBalanceAndTransactions();
  }, [fetchUserBalanceAndTransactions]);

  const tabs = [
    { id: 'makePayment', label: 'Make a Payment', icon: <DollarSign size={20} color="#FFA500" /> },
    { id: 'statements', label: 'View Statements', icon: <FileText size={20} color="#FF4500" /> },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-orange-500 via-red-500 to-red-700 flex flex-col items-center p-6">
      <div className="max-w-6xl w-full">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          <div className="bg-gradient-to-r from-orange-500 to-red-500 p-8 text-center">
            <h2 className="text-4xl font-bold text-white mb-6">Welcome to Your Dashboard</h2>
          </div>

          <div className="p-6 bg-orange-50 rounded-xl shadow-lg max-w-md mx-auto -mt-8 mb-6 text-center">
            <p className="text-gray-600 text-lg">Account Number</p>
            <p className="text-xl font-semibold text-orange-700 mb-4">{accountNumber}</p>
            <p className="text-gray-600 text-lg">Current Balance</p>
            <p className="text-3xl font-bold text-orange-600">R{balance.toFixed(2)}</p>
          </div>

          <div className="flex gap-4 justify-center p-4 bg-gray-100 border-b border-gray-200">
            {tabs.map((tab) => (
              <button
                key={tab.id}
                onClick={() => setSelectedTab(tab.id)}
                className={`flex items-center gap-2 px-4 py-3 rounded-lg font-medium transition-all duration-300 transform ${
                  selectedTab === tab.id
                    ? 'bg-orange-500 text-white shadow-md scale-105'
                    : 'bg-white text-gray-600 hover:bg-orange-50 border border-gray-200'
                }`}
                aria-label={tab.label}
              >
                {tab.icon}
                <span className="text-sm">{tab.label}</span>
              </button>
            ))}
          </div>

          <div className="p-6">
            {loading ? (
              <p className="text-center text-gray-500">Loading data...</p>
            ) : error ? (
              <p className="text-center text-red-500">{error}</p>
            ) : (
              <div>
                {selectedTab === 'makePayment' && (
                  <PaymentForm token={token} onTransactionUpdate={fetchUserBalanceAndTransactions} balance={balance} />
                )}
                {selectedTab === 'statements' && (
                  <Statements transactions={transactions} />
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserHome;
