// src/components/UserHome.js
import React, { useEffect, useState, useCallback } from 'react';
import { DollarSign, FileText, LogOut } from 'lucide-react';
import PaymentForm from './PaymentForm';
import Statements from './Statements';
import { getBalanceAndTransactions, getUserProfile } from '../api';
import { useNavigate } from 'react-router-dom';

const UserHome = ({ token }) => {
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [accountNumber, setAccountNumber] = useState('');
  const [userName, setUserName] = useState('');
  const [selectedTab, setSelectedTab] = useState('makePayment');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const fetchUserBalanceAndTransactions = useCallback(async () => {
    setLoading(true);
    setError(null);
    try {
      const balanceResponse = await getBalanceAndTransactions(token);
      setBalance(balanceResponse.data.balance);
      setTransactions(balanceResponse.data.transactions);

      const profileResponse = await getUserProfile(token);
      setAccountNumber(profileResponse.data.accountNumber);
      setUserName(`${profileResponse.data.name} ${profileResponse.data.surname}`);
    } catch (error) {
      setError('Could not load data. Please try again.');
    } finally {
      setLoading(false);
    }
  }, [token]);

  useEffect(() => {
    fetchUserBalanceAndTransactions();
  }, [fetchUserBalanceAndTransactions]);

  const tabs = [
    { id: 'makePayment', label: 'Make a Payment', icon: <DollarSign size={20} /> },
    { id: 'statements', label: 'View Statements', icon: <FileText size={20} /> },
  ];

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div className="min-h-screen flex flex-col">
      {/* Top Navigation Bar */}
      <div className="flex justify-between items-center px-6 py-4 bg-white shadow-md">
        <div className="text-2xl font-bold text-orange-600">
          Customer Portal
        </div>
        <div className="flex items-center space-x-4">
          <span className="text-gray-700">Hello, {userName}</span>
          <button
            onClick={handleLogout}
            className="flex items-center px-3 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 transition"
          >
            <LogOut size={18} className="mr-1" />
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-grow bg-gradient-to-br from-orange-500 via-red-500 to-red-700 flex flex-col items-center p-6">
        <div className="max-w-6xl w-full">
          <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
            {/* Header Section */}
            <div className="bg-gradient-to-r from-orange-500 to-red-500 p-8 text-center">
              <h2 className="text-4xl font-bold text-white mb-2">Dashboard</h2>
              <p className="text-orange-200">Manage your account and payments</p>
            </div>

            {/* Balance and Account Display */}
            <div className="p-6 bg-orange-50 rounded-xl shadow-lg max-w-md mx-auto -mt-8 mb-6 text-center">
              <p className="text-gray-600 text-lg">Account Number</p>
              <p className="text-xl font-semibold text-orange-700 mb-4">{accountNumber}</p>
              <p className="text-gray-600 text-lg">Current Balance</p>
              <p className="text-3xl font-bold text-orange-600">R{balance.toFixed(2)}</p>
            </div>

            {/* Navigation Tabs */}
            <div className="flex flex-wrap gap-4 justify-center p-4 bg-gray-100 border-b border-gray-200">
              {tabs.map((tab) => (
                <button
                  key={tab.id}
                  onClick={() => setSelectedTab(tab.id)}
                  className={`flex items-center gap-2 px-4 py-3 rounded-lg font-medium transition-all duration-300 transform ${
                    selectedTab === tab.id
                      ? 'bg-orange-500 text-white shadow-md scale-105'
                      : 'bg-white text-gray-600 hover:bg-orange-50 border border-gray-200'
                  }`}
                  aria-label={tab.label}
                >
                  {React.cloneElement(tab.icon, {
                    color: selectedTab === tab.id ? '#FFFFFF' : '#FF4500',
                  })}
                  <span className="text-sm">{tab.label}</span>
                </button>
              ))}
            </div>

            {/* Content Section */}
            <div className="p-6">
              {loading ? (
                <p className="text-center text-gray-500">Loading data...</p>
              ) : error ? (
                <p className="text-center text-red-500">{error}</p>
              ) : (
                <div>
                  {selectedTab === 'makePayment' && (
                    <PaymentForm
                      token={token}
                      onTransactionUpdate={fetchUserBalanceAndTransactions}
                      balance={balance}
                    />
                  )}
                  {selectedTab === 'statements' && (
                    <Statements transactions={transactions} />
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="bg-gray-900 py-4">
        <div className="text-center text-gray-400">
          Â© {new Date().getFullYear()} Customer Portal. All rights reserved.
        </div>
      </footer>
    </div>
  );
};

export default UserHome;
